rule.example:

class Something {
    public static function main( array $as ) {
        try{ // VIOLATION - empty
        } catch (Exception $ex) {
            // do something
        } 
    }
}


class Something {
    public static function main( array $as ) {
        try{ 
            $a = $a + 1;
        } catch (Exception $ex) {
            // VIOLATION -empty
        } 
    }
}


